import React, { useState } from 'react';
import '../styles/AdminControls.css'; // Import the CSS
import SideNavTab from './SideNavTab'; // Import Sidebar Component
import Modal from './Modal'; // Import Modal Component

const AdminControlsPage = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [modalTitle, setModalTitle] = useState('');
  const [modalContent, setModalContent] = useState('');

  // Function to open modal for a specific action
  const openModal = (action) => {
    if (action === 'startElection') {
      setModalTitle('Start Election');
      setModalContent('You are about to start the election. Proceed with opening the polls.');
    } else if (action === 'pauseVoting') {
      setModalTitle('Pause Voting');
      setModalContent('You are about to pause the voting. Make sure all participants are informed.');
    } else if (action === 'endElection') {
      setModalTitle('End Election');
      setModalContent('You are about to end the election. Proceed with closing the polls and calculating results.');
    }
    setIsModalOpen(true);
  };

  // Function to close modal
  const closeModal = () => {
    setIsModalOpen(false);
    setModalTitle('');
    setModalContent('');
  };

  return (
    <div className="admin-container">
      <div className="sidebar">
        <SideNavTab />
      </div>

      <div className="main">
        <h1>ADMIN CONTROLS</h1>

        <div className="status-banner">
          <strong>STATUS OF ELECTION: </strong>NOT STARTED
        </div>

        <div className="controls">
          <div className="control-card" onClick={() => openModal('startElection')}>
            <div className="icon">▶️</div>
            <h2>PAUSE VOTING</h2>
            <p>This will temporarily suspend all voting activity. Voters in the middle of voting will be interrupted. Continue?</p>
          </div>

          <div className="control-card" onClick={() => openModal('pauseVoting')}>
            <div className="icon">⏸️</div>
            <h2>PAUSE VOTING</h2>
            <p>Temporarily suspend voting activity for system maintenance.</p>
          </div>

          <div className="control-card" onClick={() => openModal('endElection')}>
            <div className="icon">⏹️</div>
            <h2>END ELECTION</h2>
            <p>Close voting permanently and prepare for results calculation.</p>
          </div>
        </div>

        {/* Modal for each action */}
        <Modal
          isOpen={isModalOpen}
          onClose={closeModal}
          title={modalTitle}
          content={modalContent}
        />
      </div>
    </div>
  );
};

export default AdminControlsPage;
