import React, { useState } from 'react';
import '../styles/AdminControls.css'; // Import the CSS
import SideNavTab from './SideNavTab';
import Modal from './Modal'; // Assuming you have a Modal component

const AdminControlsPage = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);

  // Open the modal
  const openModal = () => {
    setIsModalOpen(true);
  };

  // Close the modal
  const closeModal = () => {
    setIsModalOpen(false);
  };

  // Placeholder function for Start Election
  const handleStartElection = () => {
    console.log("Election started.");
    closeModal(); // Close the modal after clicking "Start Election"
  };

  return (
    <div className="admin-container">
      <div className="sidebar">
        <SideNavTab />
      </div>

      <div className="main">
        <h1>ADMIN CONTROLS</h1>

        <div className="status-banner">
          <strong>STATUS OF ELECTION: </strong>NOT STARTED
        </div>

        <div className="controls">
          <div className="control-card">
            <div className="icon">▶️</div>
            {/* Button to open modal */}
            <button className="start-election-btn" onClick={openModal}>
              START ELECTION
            </button>
            <p>Initiate the voting process and open polls to all registered voters.</p>
          </div>

          <div className="control-card">
            <div className="icon">⏸️</div>
            <h2>PAUSE VOTING</h2>
            <p>Temporarily suspend voting activity for system maintenance.</p>
          </div>

          <div className="control-card">
            <div className="icon">⏹️</div>
            <h2>END ELECTION</h2>
            <p>Close voting permanently and prepare for results calculation.</p>
          </div>
        </div>
      </div>

      {/* Modal Component */}
      <Modal
        isOpen={isModalOpen}
        onClose={closeModal}
        title="Start Election"
        content="Are you sure you want to start the election?"
        onConfirm={handleStartElection}  // Placeholder function
        imageSrc="path_to_your_image.jpg" // Optional image for the modal
      />
    </div>
  );
};

export default AdminControlsPage;
