import React, { useState } from 'react';
import '../styles/AdminControls.css'; // Import the CSS
import SideNavTab from './SideNavTab'; // Import Sidebar Component
import Modal from './Modal'; // Import Modal Component

const AdminControlsPage = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [currentButton, setCurrentButton] = useState(null); // Keeps track of which button is being edited
  const [buttonText, setButtonText] = useState({
    startElection: 'START ELECTION',
    pauseVoting: 'PAUSE VOTING',
    endElection: 'END ELECTION',
  });

  // Open modal for specific button
  const openModal = (button) => {
    setCurrentButton(button);
    setIsModalOpen(true);
  };

  // Handle saving the edited text
  const handleSaveText = (newText) => {
    setButtonText((prevState) => ({
      ...prevState,
      [currentButton]: newText,
    }));
  };

  return (
    <div className="admin-container">
      <div className="sidebar">
        <SideNavTab />
      </div>

      <div className="main">
        <h1>ADMIN CONTROLS</h1>

        <div className="status-banner">
          <strong>STATUS OF ELECTION: </strong>NOT STARTED
        </div>

        <div className="controls">
          {/* Start Election */}
          <div className="control-card" onClick={() => openModal('startElection')}>
            <div className="icon">▶️</div>
            <h2>{buttonText.startElection}</h2>
            <p>Initiate the voting process and open polls to all registered voters.</p>
          </div>

          {/* Pause Voting */}
          <div className="control-card" onClick={() => openModal('pauseVoting')}>
            <div className="icon">⏸️</div>
            <h2>{buttonText.pauseVoting}</h2>
            <p>Temporarily suspend voting activity for system maintenance.</p>
          </div>

          {/* End Election */}
          <div className="control-card" onClick={() => openModal('endElection')}>
            <div className="icon">⏹️</div>
            <h2>{buttonText.endElection}</h2>
            <p>Close voting permanently and prepare for results calculation.</p>
          </div>
        </div>

        {/* Modal for editing button text */}
        <Modal
          isOpen={isModalOpen}
          onClose={() => setIsModalOpen(false)}
          onSave={handleSaveText}
          initialText={buttonText[currentButton]}
        />
      </div>
    </div>
  );
};

export default AdminControlsPage;
