import React from 'react';
import './Modal.css'; // Modal specific CSS

const Modal = ({ isOpen, onClose, title, content, onConfirm, imageSrc }) => {
  if (!isOpen) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-container">
        <div className="modal-header">
          {imageSrc && <img src={imageSrc} alt="Modal image" className="modal-image" />}
          <h2>{title}</h2>
        </div>
        <div className="modal-body">
          <p>{content}</p>
        </div>
        <div className="modal-footer">
          <button className="modal-button cancel" onClick={onClose}>Cancel</button>
          <button className="modal-button confirm" onClick={onConfirm}>Confirm</button>
        </div>
      </div>
    </div>
  );
};

export default Modal;
